<?php /** * Стартовый файл Kopolo CMS *  * @version 2.0 [20.06.2011] * @author kopolo.ru *///время запуска скрипта$start_time = explode(" ", microtime());if (is_file('config.php') && filesize('config.php')>500) {    require 'config.php';} elseif (is_file('install/noconfig.php')) {    require 'install/noconfig.php';} else {    exit('<h1>Error: config.php not found</h1>');}include "bootstrap.php";$page = Kopolo_Page::getInstance();$page->getPage($site_id, $nicks);$page->display();if (KOPOLO_DEVELOP_MODE == 1) {    //в режиме разработки выводится время выполнения скрипта и расход памяти    $stop_time = explode(" ", microtime());    $time = round(($stop_time[1] + $stop_time[0] - ($start_time[1] + $start_time[0])), 3);    echo '<small>'    .'<div style="clear:both"></div>DEV MODE on / '     . $time . ' sec. / '     . round((memory_get_usage()/1024), 2) . ' (&#8593;'.round((memory_get_peak_usage()/1024), 2).') Kb'    .'</small>';}?>